/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure,
footer, header, hgroup, menu, nav, section {
  display: block;
}

body {
  line-height: 1;
}

ol, ul {
  list-style: none;
}

blockquote, q {
  quotes: none;
}

blockquote:before, blockquote:after,
q:before, q:after {
  content: "";
  content: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

.counter {
  cursor: pointer;
  float: right;
  margin-right: 1em;
  font-size: 1em;
  color: white;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 70px;
  background: #ea6527;
  font-size: 1.5em;
}

.head-text {
  padding-left: 2.5em;
  font-size: 1.5em;
  color: white;
  font-family: system-ui;
  font-weight: 600;
  cursor: pointer;
}

.hero-image {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
}
.hero-image p {
  display: inline-block;
  margin-top: 3em;
  margin-left: -0.5em;
  font-size: 1.5em;
}
.hero-image h1 {
  font-size: 1.5em;
  text-align: center;
  padding: 1em;
}

.text-Input {
  float: right;
  text-align: center;
  margin-right: 1em;
  padding: 1em 0;
  border-radius: 1em;
  border: none;
  outline: none;
  margin-top: 0;
}

.catDiv {
  display: inline-block;
}
.catDiv img {
  width: 100%;
  margin: 0 auto;
}
.catDiv p {
  padding: 10px 0;
  color: #47494c;
  line-height: 1.4em;
}
.catDiv h1 {
  font-size: 2em;
  margin-top: 0.5em;
  padding: 10px;
  text-align: center;
}

#form {
  margin-top: 0;
}

.cat-image {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-gap: 40px;
  padding: 10px 40px;
}

#favbtn {
  border: none;
  background: #3ca86d;
  padding: 1em 2em;
  margin-top: 2em;
  margin-left: 1em;
  border-radius: 1em;
  color: white;
  font-weight: 600;
  outline: none;
}
#favbtn:hover {
  background: red;
}

.fa-cart-plus {
  color: white;
}
